curl --location --request POST https://micro-stg-api.intigral-ott.net/discount/v1/CSRB2CGDMSTGExy0sVDlZMzNDdUyZ/batch' \

--header 'signature: 9f14e09e3f8c25f892a32d81eccf079aca2a8a07b458ca0bef7041d9941a3f80' \

--header 'Content-Type: application/json' \

--data-raw '{

  "discountType": "GIFT_CARD",

  "discountUnits": 30,

  "startDate": "2021-11-09",

  "expiryDate": "2021-12-31",

  "discountConfig": {

      "billingDays": 60,

      "tiers": ["LITE_TIER", "CLASSIC_TIER", "PREMIUM_TIER"]

  },

  "localization": {

    "ar": {

      "banner": "بطاقة هدايا لمدة 60 يومًا"

    },

    "en": {

      "banner": "Gift card for 60 days"

    }

  }

}'





